//2. here getInitialProps using 'functional' COMPONENT

const xyz = ({ posts, error }) => {
	console.log("i am Index and i am rendering ...");
	let lis = posts.map((post) => <li>{post.title}</li>);
	return (
		<div>
			{/* <Navbar /> */}
			<h2>Hello i am Index page </h2>

			<ul>{!error ? lis : <li>{error}</li>}</ul>
		</div>
	);
};

xyz.getInitialProps = async () => {
	try {
		let res = await axios.get("https://jsonplaceholer.typicode.com/posts");
		const { data } = res;

		return { posts: data };
	} catch (err) {
		alert(err);
		return { error: err };
	}
};

export default xyz;
